import 'package:bloc_quiz/src/blocs/question_provider.dart';
import 'package:bloc_quiz/src/screens/home.dart';
import 'package:flutter/material.dart';

class RootOfApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return QuestionProvider(
      child: MaterialApp(
        title: 'Flutter Movie',
        onGenerateRoute: routes,
      ),
    );
  }

  Route routes(RouteSettings settings) {
    switch (settings.name) {
      case "/":
        return MaterialPageRoute(builder: (BuildContext context) {
          final questionBloc = QuestionProvider.of(context);
          questionBloc.fetchQuestions();
          questionBloc.nextQuestion(0);
          return Home();
        });
        break;
    }
    return null;
  }
}

